	private static ItemStack createItemStack(Item item, int count, String nbtString) {
		NbtCompound nbt = parseNbt(nbtString);
		ItemStack itemStack = new ItemStack(item, count);
		itemStack.setNbt(nbt);
		return itemStack;
	}

	private static NbtCompound parseNbt(String nbtString) {
		try {
			return StringNbtReader.parse(nbtString);
		} catch (CommandSyntaxException e) {
			e.printStackTrace();
			return new NbtCompound();
		}
	}
